import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { FaCertificate, FaExpandArrowsAlt, FaTimes, FaEye } from 'react-icons/fa';
import styles from './Certificates.module.css';

const Certificates = () => {
  const [selectedCertificate, setSelectedCertificate] = useState(null);
  const [showModal, setShowModal] = useState(false);

  const certificates = [
    {
      id: 1,
      title: "CodeIt Django Web Development Certificate",
      organization: "CodeIt",
      date: "2024",
      description: "Comprehensive Django web development certification covering Python, Django framework, database management, and full-stack web application development.",
      imagePath: "/assets/codeit_certificate.jpg",
      icon: <FaCertificate />,
      category: "Web Development"
    }
  ];

  const handleViewCertificate = (certificate) => {
    setSelectedCertificate(certificate);
    setShowModal(true);
  };

  const handleOpenFullSize = (imagePath) => {
    window.open(imagePath, '_blank');
  };

  const closeModal = () => {
    setShowModal(false);
    setSelectedCertificate(null);
  };

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: { duration: 0.5 }
    }
  };

  return (
    <section id="certificates" className={styles.certificates}>
      <div className={styles.container}>
        <motion.h2 
          className={styles.title}
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          viewport={{ once: true }}
        >
          📜 Certificates
        </motion.h2>

        <motion.div 
          className={styles.grid}
          variants={containerVariants}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true }}
        >
          {certificates.map((certificate) => (
            <motion.div 
              key={certificate.id} 
              className={styles.certificateCard}
              variants={itemVariants}
              whileHover={{ y: -10, scale: 1.02 }}
              transition={{ type: "spring", stiffness: 300 }}
            >
              <div className={styles.cardHeader}>
                <div className={styles.cardIcon}>
                  {certificate.icon}
                </div>
                <span className={styles.categoryBadge}>
                  {certificate.category}
                </span>
              </div>

              <div className={styles.certificateImageContainer}>
                <img 
                  src={certificate.imagePath}
                  alt={certificate.title}
                  className={styles.certificateImage}
                  onClick={() => handleViewCertificate(certificate)}
                />
                <div className={styles.imageOverlay}>
                  <button 
                    className={styles.expandBtn}
                    onClick={() => handleViewCertificate(certificate)}
                    aria-label="View full size"
                  >
                    <FaExpandArrowsAlt />
                  </button>
                </div>
              </div>

              <div className={styles.cardContent}>
                <h3 className={styles.cardTitle}>{certificate.title}</h3>
                <h4 className={styles.cardOrganization}>{certificate.organization}</h4>
                <p className={styles.cardDescription}>{certificate.description}</p>

                <div className={styles.cardFooter}>
                  <span className={styles.cardDate}>{certificate.date}</span>
                </div>
              </div>

              <div className={styles.cardActions}>
                <button 
                  className={styles.viewBtn}
                  onClick={() => handleViewCertificate(certificate)}
                  aria-label="View Certificate"
                >
                  <FaEye />
                  View Certificate
                </button>
                <button 
                  className={styles.downloadBtn}
                  onClick={() => handleOpenFullSize(certificate.imagePath)}
                  aria-label="Open Full Size"
                >
                  <FaExpandArrowsAlt />
                  Full Size
                </button>
              </div>
            </motion.div>
          ))}
        </motion.div>

        {/* Certificate Modal */}
        {showModal && selectedCertificate && (
          <motion.div 
            className={styles.modal}
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={closeModal}
          >
            <motion.div 
              className={styles.modalContent}
              initial={{ scale: 0.8, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0.8, opacity: 0 }}
              onClick={(e) => e.stopPropagation()}
            >
              <div className={styles.modalHeader}>
                <h3 className={styles.modalTitle}>{selectedCertificate.title}</h3>
                <button 
                  className={styles.closeBtn}
                  onClick={closeModal}
                  aria-label="Close modal"
                >
                  <FaTimes />
                </button>
              </div>

              <div className={styles.modalBody}>
                <img 
                  src={selectedCertificate.imagePath}
                  alt={selectedCertificate.title}
                  className={styles.modalImage}
                />
              </div>

              <div className={styles.modalFooter}>
                <button 
                  className={styles.modalDownloadBtn}
                  onClick={() => handleOpenFullSize(selectedCertificate.imagePath)}
                >
                  <FaExpandArrowsAlt />
                  Open Full Size
                </button>
              </div>
            </motion.div>
          </motion.div>
        )}

        <motion.div 
          className={styles.infoSection}
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.3 }}
          viewport={{ once: true }}
        >
          <div className={styles.infoCard}>
            <div className={styles.infoIcon}>
              <FaCertificate />
            </div>
            <div className={styles.infoContent}>
              <h3 className={styles.infoTitle}>Professional Development</h3>
              <p className={styles.infoText}>
                Committed to continuous learning and professional growth through certified courses 
                and training programs in cutting-edge technologies.
              </p>
            </div>
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default Certificates;
