import React, { useEffect, useCallback } from 'react';
import { motion } from 'framer-motion';
import { FaYoutube, FaInstagram, FaLinkedin, FaGithub } from 'react-icons/fa';
import styles from './App.module.css';
import Hero from './Hero';
import About from './About';
import Projects from './Projects';
import Experience from './Experience';
import Skills from './Skills';
import Education from './Education';
import Achievements from './Achievements';
import Contact from './Contact';

const NavBar = () => {
  const [isScrolled, setIsScrolled] = React.useState(false);

  React.useEffect(() => {
    const handleScroll = () => {
      const scrolled = window.scrollY > 10; // Change color after scrolling 10px
      setIsScrolled(scrolled);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <motion.nav
      className={`${styles.nav} ${isScrolled ? styles.scrolled : ''}`}
      initial={{ y: -100 }}
      animate={{ y: 0 }}
      transition={{ duration: 0.5 }}
    >
      <div className={styles.logo}>Ayushma Pudasaini</div>
      <div className={styles.navCenter}>
        <a href="#home">Home</a>
        <a href="#about">About</a>
        <a href="#projects">Projects</a>
        <a href="#experience">Experience</a>
        <a href="#skills">Skills</a>
        <a href="#education">Education</a>
        <a href="#achievements">Achievements</a>
        <a href="#contact">Contact</a>
      </div>
      <div className={styles.socialLinks}>
        <a href="https://youtube.com/@ayushmapoudasaini" target="_blank" rel="noopener noreferrer" aria-label="YouTube"><FaYoutube /></a>
        <a href="https://instagram.com/ayushma.poudasaini" target="_blank" rel="noopener noreferrer" aria-label="Instagram"><FaInstagram /></a>
        <a href="https://www.linkedin.com/in/ayushma-poudasaini-443677276/" target="_blank" rel="noopener noreferrer" aria-label="LinkedIn"><FaLinkedin /></a>
        <a href="https://github.com/ayushma18" target="_blank" rel="noopener noreferrer" aria-label="GitHub"><FaGithub /></a>
      </div>
    </motion.nav>
  );
};

const App = () => {
  return (
    <div className={styles.app}>
      <NavBar />
      <main id="home">
        <Hero />
      </main>
      <About />
      <Projects />
      <Experience />
      <Skills />
      <Education />
      <Achievements />
      <Contact />
    </div>
  );
};

export default App;
