import React from 'react';
import { FaCode, FaChartLine, FaCloud, FaGitAlt, FaHtml5, FaAtom } from 'react-icons/fa';
import styles from './Skills.module.css';

const Skills = () => {
  const skillCategories = [
    {
      title: "Programming Languages",
      icon: <FaCode className={styles.icon} />,
      skills: ["Python", "SQL", "C", "C++", "Dart"]
    },
    {
      title: "Data Visualization",
      icon: <FaChartLine className={styles.icon} />,
      skills: ["matplotlib", "seaborn", "Plotly", "Librasa", "Tableau"]
    },
    {
      title: "Cloud Platforms",
      icon: <FaCloud className={styles.icon} />,
      skills: ["Amazon Web Services", "Azure"]
    },
    {
      title: "Version Control",
      icon: <FaGitAlt className={styles.icon} />,
      skills: ["Git"]
    },
    {
      title: "Design & Front-end Development",
      icon: <FaHtml5 className={styles.icon} />,
      skills: ["Canva", "HTML", "CSS", "Streamlit", "Bootstrap"]
    },
    {
      title: "Machine Learning Frameworks",
      icon: <FaAtom className={styles.icon} />,
      skills: ["NumPy", "Pandas", "Scikit-learn", "TensorFlow", "Keras", "JAX"]
    }
  ];

  return (
    <section id="skills" className={styles.skills}>
      <div className={styles.container}>
        <h2 className={styles.title}>Skills & Experiences</h2>
        <div className={styles.content}>
          {skillCategories.map((category, index) => (
            <div key={index} className={styles.category}>
              <h3 className={styles.categoryTitle}>{category.title}</h3>
              <div className={styles.skillsList}>
                {category.skills.map((skill, skillIndex) => (
                  <div key={skillIndex} className={styles.skillItem}>
                    <div className={styles.skillInfo}>
                      <span className={styles.skillName}>{skill.name}</span>
                    </div>
                    <div className={styles.progressBar}>
                      <div 
                        className={styles.progress}
                        style={{ width: `${skill.level}%` }}
                      >
                        <div className={styles.progressGlow}></div>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Skills;
