import React, { useEffect, useCallback } from 'react';
import { motion } from 'framer-motion';
import { FaYoutube, FaInstagram, FaLinkedin, FaGithub } from 'react-icons/fa';
import styles from './App.module.css';
import Hero from './Hero';
import About from './About';
import Education from './Education';
import Skills from './Skills';
import Contact from './Contact';

const NavBar = () => {
  const [isScrolled, setIsScrolled] = React.useState(false);

  React.useEffect(() => {
    const handleScroll = () => {
      const aboutSection = document.getElementById('about');
      if (aboutSection) {
        const aboutTop = aboutSection.offsetTop;
        const scrollPosition = window.scrollY + 100; // Offset for earlier trigger
        setIsScrolled(scrollPosition >= aboutTop);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <motion.nav
      className={`${styles.nav} ${isScrolled ? styles.scrolled : ''}`}
      initial={{ y: -100 }}
      animate={{ y: 0 }}
      transition={{ duration: 0.5 }}
    >
      <div className={styles.logo}>Ayushma Poudasaini</div>
      <div className={styles.navCenter}>
        <a href="#home">Home</a>
        <a href="#about">About</a>
        <a href="#experience">Experience</a>
        <a href="#skills">Skills</a>
        <a href="#education">Education</a>
        <a href="#projects">Projects</a>
        <a href="#competitions">Competitions</a>
        <a href="#awards">Awards</a>
        <a href="#blog">Blog</a>
        <a href="#contact">Contact</a>
      </div>
      <div className={styles.socialLinks}>
        <a href="#youtube" aria-label="YouTube"><FaYoutube /></a>
        <a href="#instagram" aria-label="Instagram"><FaInstagram /></a>
        <a href="#linkedin" aria-label="LinkedIn"><FaLinkedin /></a>
        <a href="#github" aria-label="GitHub"><FaGithub /></a>
      </div>
    </motion.nav>
  );
};

const App = () => {
  return (
    <div className={styles.app}>
      <NavBar />
      <Hero />
      <About />
      <Education />
      <Skills />
      <Contact />
    </div>
  );
};

export default App;
